<?php
namespace Ayeo\Barcode\Test;

use Ayeo\Barcode\Printer;

class PrinterTest extends \PHPUnit_Framework_TestCase
{
    public function testBase64Output()
    {
        $printer = new Printer(500, 100);
        $base64 = $printer->getBase64('(415)7707242745227(8020)170913100114(3900)000000000038500(96)20170919');
        //confirmed and checked
        $expected = '';
        $this->assertEquals($expected, $base64);
    }

    public function testWhiteChars()
    {
        $printer = new Printer(500, 100);
        $base64 = $printer->getBase64('(415) 7707242745227 (8020) 170913100114 (3900) 000000000038500 (96) 20170919');
        //confirmed and checked
        $expected = '';
        $this->assertEquals($expected, $base64);
    }
}
